codeTemplate.template:
stream<rstring fileName, uint64 size, rstring date, rstring user, boolean isFile> ${FilenameStream} as OUT = FTPReader(${TriggerStream}) {
	param
		protocol : ftp;
		isDirReader : true;
		host : "${host}";
		path : "${path}/";
		username : "${username}";
		password : "${password}";
	output OUT :
		fileName = FileName(),
		size = FileSize(),
		date = FileDate(),
		user = FileUser(),
		isFile = IsFile();
}

stream<rstring line, int32 sequence> ${FileStream} as OUT = FTPReader(${FilenameStream} as IN) {
	param
		protocol : ftp;
		isDirReader : false;
		host : "${host}";
		path : "/${path}/";
		filename : IN.fileName;
		username : "${username}";
		password : "${password}";
	output OUT :
		line = Line(),
		sequence = Sequence();
}

stream<blob content, int32 sequence> ${FileStream} as OUT = FTPReader(${FilenameStream} as IN; ${PasswordStream} as PWD) {
	param
		protocol : ftp;
		host : "${host}";
		path : "/${path}/";
		filename : IN.fileName;
		username : "${username}";
		password : PWD.password;
	output OUT :
		content = Binary(),
		sequence = Sequence();
}
